// ============================================
// src/components/ProductCard.js - Modern React Product Card
// ============================================
import React, { memo, useCallback } from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { formatPrice } from '../utils/currency';
import './ProductCard.css';

const ProductCard = memo(({ product }) => {
  const { addToCart } = useCart();

  const handleAddToCart = useCallback(async (e) => {
    e.preventDefault();
    try {
      await addToCart(product.id);
      alert('Added to cart!');
    } catch (error) {
      alert('Error adding to cart');
    }
  }, [addToCart, product.id]);

  // Get product-specific icon and styling
  const getProductIcon = () => {
    if (product.name.toLowerCase().includes('female')) {
      return 'üå∏';
    } else if (product.name.toLowerCase().includes('male')) {
      return 'üí™';
    } else if (product.name.toLowerCase().includes('diabetes')) {
      return 'ü©∫';
    } else if (product.name.toLowerCase().includes('sleep')) {
      return 'üò¥';
    } else if (product.name.toLowerCase().includes('vitality')) {
      return '‚ú®';
    }
    return 'üåø';
  };

  const getProductGradient = () => {
    if (product.name.toLowerCase().includes('female')) {
      return 'linear-gradient(135deg, #fce7f3, #ec4899)';
    } else if (product.name.toLowerCase().includes('male')) {
      return 'linear-gradient(135deg, #dbeafe, #3b82f6)';
    } else if (product.name.toLowerCase().includes('diabetes')) {
      return 'linear-gradient(135deg, #fef3c7, #f59e0b)';
    } else if (product.name.toLowerCase().includes('sleep')) {
      return 'linear-gradient(135deg, #e0e7ff, #6366f1)';
    }
    return 'linear-gradient(135deg, #d1fae5, #10b981)';
  };

  return (
    <article className="product-card" data-testid={`product-card-${product.id}`}>
      {product.sale_price && (
        <div className="sale-badge" aria-label="On Sale">
          Sale
        </div>
      )}
      
      <Link to={`/products/${product.id}`} className="product-link" aria-label={`View ${product.name} details`}>
        <div 
          className="product-card-image" 
          style={{ background: getProductGradient() }}
          aria-hidden="true"
        >
          <span className="product-icon" role="img" aria-label={`${product.name} icon`}>
            {getProductIcon()}
          </span>
        </div>
        
        <header className="product-header">
          <h3 className="product-card-title">{product.name}</h3>
        </header>
        
        <div className="product-card-price" role="text" aria-label={`Price: ${formatPrice(product.sale_price || product.price)}`}>
          {product.sale_price ? (
            <>
              <div className="price-original">
                <span className="strikethrough" aria-label={`Original price: ${formatPrice(product.price)}`}>
                  {formatPrice(product.price)}
                </span>
                <span className="original-text">
                  Original price was: {formatPrice(product.price)}.
                </span>
              </div>
              <div className="price-current">
                <span className="current-price" aria-label={`Current price: ${formatPrice(product.sale_price)}`}>
                  {formatPrice(product.sale_price)}
                </span>
                <span className="current-text">
                  Current price is: {formatPrice(product.sale_price)}.
                </span>
              </div>
            </>
          ) : (
            <div className="price-current">
              <span className="current-price">{formatPrice(product.price)}</span>
            </div>
          )}
        </div>

        <div className="rating-section" aria-label="Product rating">
          <div className="stars" role="img" aria-label="5 star rating">‚≠ê‚≠ê‚≠ê‚≠ê‚≠ê</div>
          <span className="rating-text">
            Rated <strong>0</strong> out of 5
          </span>
        </div>
      </Link>
      
      <footer className="product-actions">
        <button 
          onClick={handleAddToCart} 
          className="btn-add-cart"
          aria-label={`Add ${product.name} to cart`}
        >
          Add to cart
        </button>
        <Link 
          to={`/products/${product.id}`} 
          className="btn-buy-now"
          aria-label={`Buy ${product.name} now`}
        >
          Buy Now
        </Link>
      </footer>
    </article>
  );
});

ProductCard.displayName = 'ProductCard';

export default ProductCard;
