import React, { useState, useCallback, useMemo } from 'react';
import { formatPrice } from '../utils/currency';
import './PackSelection.css';

const PackSelection = ({ product, onPackChange, onQuantityChange, onAddToCart, onBuyNow }) => {
  const [selectedPack, setSelectedPack] = useState(1);
  const [quantity, setQuantity] = useState(1);

  // Pack configurations
  const packConfigs = useMemo(() => [
    {
      id: 1,
      bottles: 1,
      tablets: 60,
      months: 1,
      discount: 27,
      pricePerBottle: product.sale_price || product.price,
      totalPrice: (product.sale_price || product.price) * 1,
      originalPrice: product.price * 1,
      savings: (product.price * 1) - ((product.sale_price || product.price) * 1)
    },
    {
      id: 2,
      bottles: 2,
      tablets: 120,
      months: 2,
      discount: 33,
      pricePerBottle: Math.round((product.sale_price || product.price) * 0.9),
      totalPrice: Math.round((product.sale_price || product.price) * 0.9 * 2),
      originalPrice: product.price * 2,
      savings: (product.price * 2) - Math.round((product.sale_price || product.price) * 0.9 * 2)
    },
    {
      id: 3,
      bottles: 3,
      tablets: 180,
      months: 3,
      discount: 40,
      pricePerBottle: Math.round((product.sale_price || product.price) * 0.8),
      totalPrice: Math.round((product.sale_price || product.price) * 0.8 * 3),
      originalPrice: product.price * 3,
      savings: (product.price * 3) - Math.round((product.sale_price || product.price) * 0.8 * 3)
    }
  ], [product]);

  const handlePackSelect = useCallback((packId) => {
    setSelectedPack(packId);
    onPackChange(packId);
  }, [onPackChange]);

  const handleQuantityChange = useCallback((newQuantity) => {
    if (newQuantity >= 1) {
      setQuantity(newQuantity);
      onQuantityChange(newQuantity);
    }
  }, [onQuantityChange]);

  const selectedPackConfig = packConfigs.find(pack => pack.id === selectedPack);

  return (
    <div className="pack-selection">
      <h3 className="pack-title">Pack:</h3>
      
      <div className="pack-options">
        {packConfigs.map((pack) => (
          <div
            key={pack.id}
            className={`pack-card ${selectedPack === pack.id ? 'selected' : ''}`}
            onClick={() => handlePackSelect(pack.id)}
          >
            <div className="pack-badge">
              <span className="checkmark">✓</span>
              <span className="stars">⭐⭐</span>
            </div>
            
            <div className="pack-content">
              <h4 className="pack-name">{pack.bottles} Bottle{pack.bottles > 1 ? 's' : ''}</h4>
              <div className="pack-details">
                <span className="tablets">{pack.tablets} Tablets</span>
                <span className="months">{pack.months} Month{pack.months > 1 ? 's' : ''}</span>
              </div>
              
              <div className="pack-pricing">
                <div className="current-price">{formatPrice(pack.totalPrice)}</div>
                <div className="original-price">{formatPrice(pack.originalPrice)}</div>
                <div className="savings">Save {formatPrice(pack.savings)}</div>
              </div>
              
              <div className="discount-badge">{pack.discount}%</div>
            </div>
          </div>
        ))}
      </div>

      <div className="quantity-section">
        <label className="quantity-label">Quantity:</label>
        <div className="quantity-controls">
          <button 
            className="quantity-btn minus"
            onClick={() => handleQuantityChange(quantity - 1)}
            disabled={quantity <= 1}
          >
            —
          </button>
          <span className="quantity-value">{quantity}</span>
          <button 
            className="quantity-btn plus"
            onClick={() => handleQuantityChange(quantity + 1)}
          >
            +
          </button>
        </div>
      </div>

      <div className="action-buttons">
        <button className="btn-add-cart" onClick={onAddToCart}>
          ADD TO CART
        </button>
        <button className="btn-buy-now" onClick={onBuyNow}>
          BUY NOW
        </button>
      </div>
    </div>
  );
};

export default PackSelection;
